import React from 'react';
import PropTypes, { InferProps } from "prop-types";
import axios from 'axios';

const MINUTE = 60000;

const loadData = (currency: string, setData: React.Dispatch<React.SetStateAction<String>>) => {
  axios.get(`/price/${currency}`)
    .then(response => {
      console.log(response.data);
    })
    .catch(error => {
      console.log(error);
    });
};

export default function CryptoCurrency ({ currency }: InferProps<typeof CryptoCurrency.propTypes>) {
  const [data, setData] = React.useState({});

  React.useEffect(() => {
    loadData(currency, setData);
    const interval = setInterval(() => {
      loadData(currency, setData);
    }, MINUTE);

    return () => clearInterval(interval);
  }, [currency]);

  return (
    <div>{currency}</div>
  )
}

CryptoCurrency.propTypes = {
  currency: PropTypes.string.isRequired
};
